import React, { Component } from 'react';
import './Player.css';

class Player extends Component {
  constructor() {
    super();

    this.state = {
      file: "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3"
    };
  }

  togglePlay() {
    this.setState({ is_playing: !this.state.is_playing });
  }

  setProgress(e) {
    console.log(offsetLeft(this.refs.progress_bar), this.refs.progresse.clientX);  //.progress_bar.offsetLeft, e.clientX);
  }

  render() {
    if (this.refs.player) {
      const player = this.refs.player;

      if (player.paused) {
        if (this.state.is_playing) {
          player.play();
        }
      }
      else if (!this.state.is_playing) {
        player.pause();
      }
    }

    var playerClassName = {
      "fa": true,
      "fa-play": !this.state.is_playing,
      "fa-pause": this.state.is_playing
    }

    return (
      <div className="player">
        <div className="controls">
          <a href="javascript:void();">
            <i className="fas fa-chevron-left"></i>
          </a>
          <a onClick={this.togglePlay.bind(this)}>
            <i className={classnames(playerClassName)} aria-hidden="true"></i>
          </a>
          <a href="javascript:void();">
            <i className="fas fa-chevron-right"></i>
          </a>
        </div>
        <div onClick={this.setProgress.bind(this)} className="progress">
          <div ref="progress_bar" className="bar">
            <div></div>
          </div>
        </div>
        <audio ref="player">
          <source src={this.state.file} autoPlay="true"/>
        </audio>
      </div>

    );
  }
}

function offsetLeft(el) {
  var left = 0;
  while (el && el !== document) {
    left += el.offsetLeft;
    el = el.offsetParent;
  }
  return left;
}

function classnames(obj) {
  var css = [];
  Object.keys(obj).forEach((key) => {
    if (obj[key]) {
      css.push(key);
    }
  });
  return css.join(' ');
}


export default Player;
